import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import { DropTarget } from 'react-dnd';


const useStyles = makeStyles(theme => ({
    root: {
      padding: theme.spacing(3, 2),
    },
    paper : {
      height : 500,
      textAlign : "center",
      backGroundColor : 'lightgreen'
    }

  }));

  const spec = {
    drop(props, monitor, component){
        const types = monitor.getItem()
        console.log(types, monitor)
    }
  }
  function collect(connect, monitor) {
  return {
      connectDropTarget: connect.dropTarget(),
      hovered: monitor.isOver(),
      isOverCurrent: monitor.isOver({ shallow: true }),
      canDrop: monitor.canDrop()
  };
  }

const TargetDropArea = ({hovered, canDrop, connectDropTarget, droppedItem}) => {
     const classes = useStyles();
     const backGroundColor = hovered ? 'lightgreen' : 'white'
     return connectDropTarget(
        <div className = {{backGroundColor : backGroundColor}}>
            <Paper className={classes.paper}>Drag from left</Paper>
        </div>
    )
}



export default  DropTarget("SOURCE", spec, collect)(TargetDropArea)
